function delete_alert(url, selfurl, selected) {
    link = url;
    selflink = selfurl;
    current = selected;
    bootbox.confirm({
        message: "<i class=\"fa fa-warning btn-sm bigger-150 orange\"></i>  \n\
                <strong class=\"orange bold bigger-120\"> Attention!!! Etes vous sûr de vouloir continuer cette action?</strong>",
        buttons: {
            confirm: {
                label: "Continuer  <i class=\"fa fa-arrow-right\"></i>",
                className: "ace-icon btn btn-primary btn-sm bigger-120",
            },
            cancel: {
                label: "<i class=\"fa fa-undo\"></i> Abandonner",
                className: "ace-icon btn btn-danger btn-sm bigger-120",
            }
        },
        callback: function (result) {
            if (result)
                window.location.href = link;
            else {
                window.location.href = selflink;
            }
        }
    }

    );
}
